/**
 * Called when the application from background to foreground
 * @public
 */
export declare function useAppActive(fn: () => void): void;

/**
 * Called when the application from foreground to background
 * @public
 */
export declare function useAppInactive(fn: () => void): void;

/**
 * Called when the page is hidden or in the application from foreground to background
 * @public
 */
export declare function useHide(fn: () => void): void;

/**
 * Called when the component is mounted
 * @public
 */
export declare function useMount(fn: () => void): void;

/**
 * Called after the page window resize
 * @public
 */
export declare function useResize(fn: () => void): void;

/**
 * Called when the page is displayed or in the application from background to foreground
 * @public
 */
export declare function useShow(fn: () => void): void;

/**
 * Called when the component is unmounted
 * @public
 */
export declare function useUnmount(fn: () => void): void;

